import './App.css';
import React, {Component} from 'react';

class App extends Component {

	state = {
		results: [],
		search: ''
	}

	handleChange = (e) => {
		this.setState({search: e.target.value});
	}

	handleSearch = (e) => {
		alert(this.state.search);
	}

	render(){
		return(
			<div>
				<form onSubmit={this.handleSearch}>
					<label>
						<p>Search Naperville: </p><input type="text" value={this.state.search} onChange={this.handleChange} />
					</label>
					<input type="submit" value="Search"/>
				</form>
			</div>
		)
	}
}

export default App;
