import {Component} from 'react';
import Results from './Results';
const axios = require("axios");
const MY_API_KEY = "dI0U12otFbB2R3lTzXpowD0FBRfLW5WYuFeNjPmrWo2qVyqnQBOzCmMzprP-nVAD2HnDDfj1Bg9dCQypQBb0ZH0J82IG0udm8xG8UvDloQ7hwwpUjNKaFaiQgRRsYHYx"

let yelpREST = axios.create({
  baseURL: "https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/",
  headers: {
    Authorization: `Bearer ${MY_API_KEY}`,
    "Content-type": "application/json",
  },
})

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			results: [],
			search: ''
		}
	  }

	state = {
		results: [],
		search: ''
	}

	handleChange = (e) => {
		this.setState({search: e.target.value});
	}

	handleSearch = (e) => {
		yelpREST("/businesses/search", { params: { location: "Naperville", term: "coffee" } }).then(({ data }) => {
		  alert(data)
		})
	}

	render(){
		return(
			<div>
				<label>
					<h2>Plaidypus Code Challenge</h2>
					<h6>Anthony Smith</h6>
					Search Naperville: <input type="text" value={this.state.search} onChange={this.handleChange} />
				</label>
				<button onClick={this.handleSearch}>Search</button>
				<Results data={this.state.results} />
			</div>
		)
	}
}

export default App;
