import './App.css';
import React, {Component} from 'react';

class App extends Component {

	state = {
		results: [],
		search: ''
	}

	handleChange = (e) => {
		this.setState({search: e.target.value})
	}

	render(){
		return(
			<div>
				<form onSubmit={this.handleSearch}>
					<label>
					<input type="text" value={this.state.search} onChange={this.handleChange} />
					</label>
				</form>
			</div>
		)
	}
}

export default App;
